import { useParams } from 'react-router-dom';
import React from 'react';
import { useState, useEffect } from 'react';
import axios from 'axios';

export default function BillEdit() {
  const { billId } = useParams();
  //console.log(billId);
  const [formData, setFormData] = useState({
    patientName: '',
    address: '',
    hospitalName: '',
    dateOfService: '',
    billAmount: '',
    billImage: null,
});
const [errorMessage, setErrorMessage] = useState('');

useEffect(() => {
    axios
      .get(`http://localhost:8000/bills/${billId}`)
      .then((response) => {
        const existingData = {
            patientName: response.data.patient

        }
        console.log('Existing data:', existingData);
        setFormData(existingData);
      })
      .catch((error) => {
        console.error('Error fetching existing data:', error);
        setErrorMessage('Error: Failed to fetch existing data from the server.');
      });
  }, []);


  // Use `billId` to fetch the specific bill data or perform any other operations

  return (
    <div>
      {/* Render the specific Bill page based on `billId` */}
      <h1>Bill Edit - {billId}</h1>
    </div>
  );
}
