import React, { useState } from 'react';
import axios from 'axios';

const FormPage = () => {
  const [formData, setFormData] = useState({
    patientName: '',
    address: '',
    hospitalName: '',
    dateOfService: '',
    billAmount: '',
    billImage: null,
  });

  const handleInputChange = (e) => {
    if (e.target.name === 'billImage') {
      setFormData({
        ...formData,
        [e.target.name]: e.target.files[0],
      });
    } else {
      setFormData({
        ...formData,
        [e.target.name]: e.target.value,
      });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const postData = new FormData();
    postData.append('patientName', formData.patientName);
    postData.append('address', formData.address);
    postData.append('hospitalName', formData.hospitalName);
    postData.append('dateOfService', formData.dateOfService);
    postData.append('billAmount', formData.billAmount);
    postData.append('billImage', formData.billImage);

    axios
      .post('http://localhost:8000/postbill', postData)
      .then((res) => {
        console.log('Form submitted successfully:', res);
        // Perform any additional actions on successful form submission
      })
      .catch((error) => {
        console.error('Error during form submission:', error);
        // Handle form submission error
      });
  };

  return (
    
    <form onSubmit={handleSubmit}>
        <div className="form-container">
            <div className="input">
      <label>
        Patient Name:
        <input
          type="text"
          name="patientName"
          value={formData.patientName}
          onChange={handleInputChange}
          required
        />
      </label>
        </div>
        <div className="input">
      <label>
        Address:
        <input
          type="text"
          name="address"
          value={formData.address}
          onChange={handleInputChange}
          required
        />
      </label>
      </div>
      <div className="input">
      <label>
        Hospital Name:
        <input
          type="text"
          name="hospitalName"
          value={formData.hospitalName}
          onChange={handleInputChange}
          required
        />
      </label>
      </div>
      <label>
        Date of Service:
        <input
          type="date"
          name="dateOfService"
          value={formData.dateOfService}
          onChange={handleInputChange}
          required
        />
      </label>
      <label>
        Bill Amount:
        <input
          type="number"
          name="billAmount"
          value={formData.billAmount}
          onChange={handleInputChange}
          required
        />
      </label>
      <label>
        Bill Image:
        <input
          type="file"
          name="billImage"
          onChange={handleInputChange}
          required
        />
      </label>
      <button type="submit">Submit</button>
      </div>
    </form>

  );
};

export default FormPage;
